mapper.insert:
insert
into Member(userID, userIdentity, teamID)
VALUES (#{userID}, #{userIdentity}, #{teamID});

mapper.select:
select count(distinct B.userID)
from Member as A, Member as B
where A.userID = #{userID} and A.teamID = B.teamID and A.userID != B.userID;

mapper.update:
update Member
set userIdentity = #{userIdentity}
where userID = #{userID}

mapper.select:
select *
from Team
where Team.teamID in (select Member.teamID from Member where Member.userID = #{userID});

select *
from DocUser
where DocUser.userID in (select Member.userID from Member where Member.teamID = #{teamID});

select userName, userIdentity
from Member,DocUser
where Member.teamID = #{teamID} and Member.userID = DocUser.userID;
